        void UploadToCCD()
        {
            if (string.IsNullOrEmpty(ASCCDHelper.BucketUuid) || string.IsNullOrEmpty(ASCCDHelper.BucketName) || string.IsNullOrEmpty(ASCCDHelper.BadgeName))
            {
                EditorUtility.DisplayDialog("Upload Failed", "Please select a bucket/badge to use before upload to CCD.", "OK");
                return;
            }

            if (!CheckTextureOk())
            {
                if (!EditorUtility.DisplayDialog("Upload Warning", "Please build texture AssetBundles before uploading.\nContinue to upload ?", "Yes", "Cancel"))
                {
                    return;
                }
            }

            if (!CheckSceneOk())
            {
                if (!EditorUtility.DisplayDialog("Upload Warning", "Please build scene AssetBundles before uploading.\nContinue to upload ?", "Yes", "Cancel"))
                {
                    return;
                }
            }

            // The platform-specific build target directory needs to be consistent between the C++ side and
            // the C# side. Ideally there should be an API that allows the C# package to inform the engine
            // of the directory, but I don't know yet how important/generic that API would be, so for now we
            // have to keep the implicit protocol between the engine and the C# package, meaning we should
            // call GetBuildTargetUniqueName() in C++ and BuildPipeline.GetBuildTargetName() in C# for the
            // consistent directory.
            string buildTargetName = BuildPipeline.GetBuildTargetName(EditorUserBuildSettings.activeBuildTarget);

            var toBeUploaded = new List<System.Tuple<CCDResourceType, string>>();
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_TextureABPath + "/" + buildTargetName));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_SceneABPath + "/" + buildTargetName));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_MeshABPath));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_AudioABPath));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_AnimationABPath));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.StreamedResource, ASBuildConstants.k_FontABPath));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.CustomCloudAssets, ASBuildConstants.k_CustomCloudAssetsPath));
            toBeUploaded.Add(Tuple.Create(CCDResourceType.AddressableAsset, ASBuildConstants.k_AddressabeAssetsPath));
            BeforeCCDUpload?.Invoke(toBeUploaded);

            bool result = ASCCDHelper.SyncEntries(toBeUploaded);

            if (result)
            {
                string releaseId = ASCCDHelper.CreateRelease("Upload");
                ASCCDHelper.CreateOrUpdateBadge(ASCCDHelper.BadgeName, releaseId);
                Debug.Log("All files have been uploaded successfully.");
            }
            else
            {
                Debug.LogError("Some files failed to upload, please try to upload again.");
            }
            RefreshBadgesInfo(ASCCDHelper.BadgeName);
        }

