        bool CheckTextureOk()
        {
            bool foundAnyPlaceholders = false;

            var allTexs = AutoStreamingSettings.textures;
            foreach (var item in allTexs)
            {
                if (item.usePlaceholder)
                {
                    foundAnyPlaceholders = true;
                    break;
                }
            }

            if (!foundAnyPlaceholders)
            {
                return true;
            }

            string texturePath = ASUtilities.GetPlatformSpecificResourcePath(ASBuildConstants.k_TextureABPath);
            return Directory.Exists(texturePath) && Directory.GetFiles(texturePath).Length != 0;
        }

        bool CheckSceneOk()
        {
            bool foundAnyStreamingScene = false;
            var allScenes = AutoStreamingSettings.scenes;
            foreach (var item in allScenes)
            {
                if (item.onDemandDownload)
                {
                    foundAnyStreamingScene = true;
                    break;
                }
            }

            if (!foundAnyStreamingScene)
            {
                return true;
            }

            string scenePath = ASUtilities.GetPlatformSpecificResourcePath(ASBuildConstants.k_SceneABPath);
            return Directory.Exists(scenePath) && Directory.GetFiles(scenePath).Length != 0;
        }
